import { Component, Prop, Vue } from 'vue-property-decorator';
import { Panel, NoResults, ErrorBanner, AppButton, ButtonGroup, InputValidate, Modal, AppTable, Pagination, SelectValidate, InputField } from '@/components';
import { IFee, VForm } from '@/interfaces';
import { ValidationObserver } from 'vee-validate';
import api from '@/api';
import deepCopy from '@/utils';
import { ConfirmationModal } from '@/patterns';

@Component({
  components: {
    Panel,
    NoResults,
    ErrorBanner,
    AppButton,
    ButtonGroup,
    InputValidate,
    ValidationObserver,
    Modal,
    AppTable,
    Pagination,
    SelectValidate,
    ConfirmationModal,
    InputField
  }
})
export default class Fees extends Vue {
  fees: IFee[] = [];
  editItem: IFee = {};
  showModal = false;
  columns = [
    { label: 'Name', selector: 'name' },
    { label: 'Amount', slotName: 'amount' },
    { label: 'Type', selector: 'type' },
    { label: 'Actions', slotName: 'actions' },
  ];
  errorList: string[] = [];
  isEdit = false;
  showConfirmModal = false;
  deleteItem: IFee = {};

  created() {
    this.getFees();
    this.resetEditItem();
  }

  getFees() {
    api.get('fee/all').then( resp => {
      this.fees = resp.data;
    })
  }

  openModal(obj) {
    if(obj) {
      this.editItem = deepCopy(obj);
      this.isEdit = true;
    }
    this.showModal = true;
  }

  closeModal() {
    this.showModal = false;
    this.resetEditItem();
    this.isEdit = false;
  }

  showConfirm(obj) {
    this.deleteItem = obj;
    this.showConfirmModal = true;
  }

  hideConfirm() {
    this.showConfirmModal = false;
    this.deleteItem = {};
  }

  resetEditItem() {
    this.editItem = {};
  }

  submitForm() {
    (this.$refs.form as VForm).validate().then( valid => {
      if(valid) {
        if(this.isEdit) {
          this.submitEdit();
        }
        else {
          this.submitNew();
        }
      }
    });
  }

  submitNew() {
    api.post('fee/', this.editItem).then( resp => {
      this.getFees();
      this.$toast.success('Fee Added');
    })
    .catch(error => {
      this.errorList.push(error.response.data.message);
    });
    this.closeModal();
  }

  submitEdit() {
    api.put('fee/' + this.editItem.id, this.editItem).then( resp => {
      this.getFees();
      this.$toast.success('Fee Updated');
    })
    .catch(error => {
      this.errorList.push(error.response.data.message);
    });
    this.isEdit = false;
    this.closeModal();
  }

  deleteFee() {
    api.delete('fee/' + this.deleteItem.id).then( resp => {
      this.getFees();
      this.$toast.success('Fee Deleted');
    })
    .catch(error => {
      this.errorList.push(error.response.data.message);
    });
  }
}
