import { Component, Prop, Vue } from 'vue-property-decorator';

@Component
export default class Checkbox extends Vue {
  @Prop() name: string;
  @Prop() value: any;
  @Prop({default: false}) checked: boolean;

  onChange(evt: any) {
    this.$parent.$emit('change', evt.target.checked, this.value);
  }
}
